    this.tipoPokemon = this.tipoPokemon.bind(this);
    const filtrar = data.filter((f) => f.type === 'Fire');
    this.state = {
      botaoPassar: 0,
      tipoPokemon: filtrar,
      btn: 0,
    }
  }

