      tipoPokemon: atualizar.tipoPokemon = data,
    }));
  }

  tipoPokemon() {
    const filtrar = data.filter((f) => f.type === 'Fire');
    // const listaPokemon = filtrar.map((lista) =><Pokemon data={lista}  key={lista.id} />)

    if (this.state.botaoPassar >= filtrar.length-1) {
      this.setState((atualizar, _prop) => ({
         botaoPassar: atualizar.botaoPassar = -1,
      }));
    }

     this.setState((atualizar, _props) =>({
      tipoPokemon: atualizar.tipoPokemon = filtrar,
      botaoPassar: atualizar.botaoPassar + 1,
    }));
  }

  render() {
    return(
      <div className='pokedex'>
        <header className='titulo'>
          Pokedex
        </header>
        <section className='listaPokemon'>
          <Pokemon data={ this.state.tipoPokemon[this.state.botaoPassar] } />
          <button onClick={this.passarPokemon}>Passar Pokemon</button>
          <button onClick={this.tipoPokemon}>Fogo</button>
        </section>
      </div>
    );
  }
}

export default Pokedex;
