    this.state = {
      pokeBotao: 0,
    }
  }

  passarPokemon() {
  //   const listaPokemon = data.map((lista) => <li><Pokemon pokeInfo={lista} key={lista.id} /></li>)
  //   this.setState((atualizando, _props) => ({
