import React, { Component } from 'react';
import ImageDog from './ImageDog';

class Dog extends Component {
  constructor(props) {
    super(props)

    this.state = {
      imageDog: '',
      galleryDog: [],
      loading: true,
    }
  }

  fetchDog = async () => {
    this.setState({ loading: true },
      async () => {
      const url = 'https://dog.ceo/api/breeds/image/random'
      const response = await fetch(url);
      const apiDog = await response.json();

      this.setState({
        loading: false,
        imageDog: apiDog.message,
      });
    });
  }

  saveImageDog = () => {
    this.setState((state) => {
      return {
        galleryDog: [ ...state.galleryDog, state.imageDog ]
      }
    });
  }

  componentDidMount() {
    this.fetchDog();
  }

  render() {
    const { imageDog, loading, galleryDog } = this.state; 
    const messageLoading = <span>Loading...</span>
    console.log(galleryDog.length)
    return(
      <div>
        <h1>
          Galeria de cães
        </h1>
        <ImageDog imagens={ galleryDog } />
        { loading ? messageLoading : <ImageDog imagens={ imageDog } /> }
        <button onClick={ this.fetchDog }>Adicionar nova imagem</button>
        <button onClick={ this.saveImageDog }>Salvar imagem</button>
      </div>  
    );
  }
}

export default Dog;
